let startGame = (players) => {
        return {
            type: 'GAME_START',
            players
        };
    },
    beginRound = () => { return { type: 'ROUND_BEGIN' }; },
    endRound = () => (dispatch, getState) => {
        dispatch({ type: 'ROUND_END' });
        
        if (getState().get('roundsRemains') == 0) {
            dispatch({ type: 'GAME_END' });
        }
    },
    playCard = (player, card) => {
        return {
            type: 'PLAY_CARD',
            player,
            card
        };
    };


export {
    startGame,
    beginRound,
    endRound,
    playCard
};
