import { expect } from 'chai';

import {
    makeResourceBag,
    transferResources,
    transferResourcesExact,
    transferGold
} from './common';

describe('makeResourceBag', () => {
    it('должна возвращать "пустой" мешок при вызове без параметров', () => {
        expect(makeResourceBag().toJS()).to.eql({
            sand: 0,
            wood: 0,
            clay: 0,
            stone: 0,
            silver: 0,
            gold: 0
        });
    });

    it('должна возвращать мешок с указанными значениями ресурсов', () => {
        expect(makeResourceBag({ gold: 20 }).toJS()).to.eql({
            sand: 0,
            wood: 0,
            clay: 0,
            stone: 0,
            silver: 0,
            gold: 20
        });
    });

    it('должна игнорировать лишние поля', () => {
        expect(makeResourceBag({ sand: 20, water: 100 }).toJS()).to.eql({
            sand: 20,
            wood: 0,
            clay: 0,
            stone: 0,
            silver: 0,
            gold: 0
        });
    });
});
