const getPlayerPath = (state, player) => {
          const index = state.get('players').findIndex(item => item.get('color') == player);
          
          if (index == -1) {
              throw 'incorrect player color';
          }
          
          return ['players', index];
      },
      error = (state, error) => {
          return state.set('error', error);
      };

export {
    getPlayerPath,
    error
}
